import React, { useState } from "react";
import "./App.css";

function App() {
  const [country, setCountry] = useState("");
  const [taste, setTaste] = useState("");
  const [recommendations, setRecommendations] = useState([]);

  const handleCountryChange = (event) => {
    setCountry(event.target.value);
  };

  const handleTasteChange = (event) => {
    setTaste(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch("/recommend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ country, taste }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setRecommendations(data);
    } catch (error) {
      console.error("A problem occurred with the fetch operation:", error);
    }
  };

  // Utility function to truncate descriptions to 50 words
  const truncateDescription = (description) => {
    const words = description.split(" ");
    if (words.length > 60) {
      return words.slice(0, 60).join(" ") + "...";
    } else {
      return description;
    }
  };

  return (
    <div className="container">
      <h1 className="title">VinoVista</h1>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>
              Country:
              <input
                type="text"
                value={country}
                onChange={handleCountryChange}
                className="input-field"
              />
            </label>
          </div>
          <div className="form-group">
            <label>
              Taste preferences:
              <input
                type="text"
                value={taste}
                onChange={handleTasteChange}
                className="input-field"
              />
            </label>
          </div>
          <button type="submit" className="submit-button">
            FIND WINES
          </button>
        </form>
      </div>
      <div className="recommendations">
        {recommendations.map((wine, index) => (
          <div key={index} className="recommendation-item">
            <h3>{wine.title}</h3>
            <p className="province">Province: {wine.province}</p>
            <p>{truncateDescription(wine.description)}</p>
            <p className="price">Price: ${wine.price}</p>
            <p className="points">Rating: {wine.points} points</p>
            <p className="similarity">
              Cosine Similarity: {wine.cosine_similarity.toFixed(2)}
            </p>
            <p className="similarity">
              Euclidean Similarity: {wine.euclidean_similarity.toFixed(2)}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
